import React, { useMemo, useState, useRef, useEffect, useCallback } from "react";
import { useSelector, useDispatch } from "react-redux";
import ContentCard from "./ContentCard";
import { fetchContents, setFiltersFromUrl } from "../features/contentSlice";


function parseFiltersFromUrl() {
  const params = new URLSearchParams(window.location.search);
  const [minPrice, maxPrice] = filters.pricing.range || [0, 999];

  const filters = {
    pricing: {
      paid: params.has("paid"),
      free: params.has("free"),
      viewOnly: params.has("viewOnly"),
    },
    keyword: params.get("q") || "",
    sort: "name",
  };
  return filters;
}

export default function ContentGrid() {
  const dispatch = useDispatch();
  const { items, status, error, filters } = useSelector((s) => s.content);

  // On mount, parse URL and set filters in slice
  useEffect(() => {
    const urlFilters = parseFiltersFromUrl();
    dispatch(setFiltersFromUrl(urlFilters));
  }, [dispatch]);

  // Fetch contents once
  useEffect(() => {
    if (status === "idle") {
      dispatch(fetchContents());
    }
  }, [status, dispatch]);

  // Client-side filtered list (memoized)
  // const filtered = useMemo(() => {
  //   if (!items || items.length === 0) return [];
  //   // Determine if pricing filter is active (none selected -> show all)
  //   const { paid, free, viewOnly } = filters.pricing;
  //   const pricingActive = paid || free || viewOnly;

  //   const lowKeyword = (filters.keyword || "").toLowerCase();

  //   return items.filter((it) => {
  //     // Decide pricing type for item:
  //     // heuristic: check item.price or item.pricing / item.type string
  //     let isPaid = false;
  //     if (it.price != null && it.price !== 0) isPaid = true;
  //     if (String(it.pricing || "").toLowerCase().includes("paid")) isPaid = true;

  //     let isFree = false;
  //     if ((it.price === 0) || String(it.pricing || "").toLowerCase().includes("free")) isFree = true;
  //     if (!isPaid && !String(it.pricing || "").toLowerCase().includes("view") && (it.price == null)) {
  //       // assume free if no price and no viewOnly marker
  //       isFree = true;
  //     }

  //     let isViewOnly = false;
  //     if (String(it.pricing || "").toLowerCase().includes("view")) isViewOnly = true;
  //     if (it.viewOnly) isViewOnly = true;

  //     // Pricing filter
  //     if (pricingActive) {
  //       let passed = false;
  //       if (paid && isPaid) passed = true;
  //       if (free && isFree) passed = true;
  //       if (viewOnly && isViewOnly) passed = true;
  //       if (!passed) return false;
  //     }

  //     // Keyword filter
  //     if (lowKeyword) {
  //       const name = (it.userName || it.username || it.user || it.creator || "").toLowerCase();
  //       const title = (it.title || it.name || "").toLowerCase();
  //       if (!name.includes(lowKeyword) && !title.includes(lowKeyword)) return false;
  //     }

  //     return true;
  //   });
  // }, [items, filters]);


  const filtered = useMemo(() => {
    if (!items || items.length === 0) return [];
    const { paid, free, viewOnly, range } = filters.pricing;
    const keyword = (filters.keyword || "").toLowerCase();

    return items.filter((it) => {
      const price = it.price ?? it.amount ?? 0;

      // Keyword filter
      if (keyword) {
        const name = (it.userName || it.username || it.user || it.creator || "").toLowerCase();
        const title = (it.title || it.name || "").toLowerCase();
        if (!name.includes(keyword) && !title.includes(keyword)) return false;
      }

      // Pricing type filter
      let isPaid = price > 0 || String(it.pricing || "").toLowerCase().includes("paid");
      let isFree = price === 0 || String(it.pricing || "").toLowerCase().includes("free");
      let isViewOnly = String(it.pricing || "").toLowerCase().includes("view") || it.viewOnly;

      const pricingActive = paid || free || viewOnly;
      if (pricingActive) {
        let passed = false;
        if (paid && isPaid) passed = true;
        if (free && isFree) passed = true;
        if (viewOnly && isViewOnly) passed = true;
        if (!passed) return false;
      }

      // ✅ Price slider filter
      if (paid && (price < range[0] || price > range[1])) return false;

      return true;
    });
  }, [items, filters]);

  // Infinite scroll state
  const PAGE_SIZE = 12; 
  const [visibleCount, setVisibleCount] = useState(PAGE_SIZE);

  useEffect(() => {
    setVisibleCount(PAGE_SIZE);
  }, [filters.keyword, filters.pricing.paid, filters.pricing.free, filters.pricing.viewOnly]);
  // useEffect(() => {
  //   const urlFilters = parseFiltersFromUrl();
  //   if (
  //     urlFilters.keyword !== filters.keyword ||
  //     urlFilters.pricing.paid !== filters.pricing.paid ||
  //     urlFilters.pricing.free !== filters.pricing.free ||
  //     urlFilters.pricing.viewOnly !== filters.pricing.viewOnly
  //   ) {
  //     dispatch(setFiltersFromUrl(urlFilters));
  //   }
  // }, [dispatch, filters]);


  // intersection observer
  const loaderRef = useRef(null);
  const handleObserver = useCallback(
    (entries) => {
      const target = entries[0];
      if (target.isIntersecting) {
        setVisibleCount((prev) => Math.min(prev + PAGE_SIZE, filtered.length));
      }
    },
    [filtered.length]
  );

  useEffect(() => {
    const option = {
      root: null,
      rootMargin: "0px",
      threshold: 0.1,
    };
    const observer = new IntersectionObserver(handleObserver, option);
    if (loaderRef.current) observer.observe(loaderRef.current);
    return () => observer.disconnect();
  }, [handleObserver]);

  if (status === "loading") {
    return <div className="center">Loading...</div>;
  }
  if (status === "failed") {
    return <div className="center">Error: {error}</div>;
  }

  const toRender = filtered.slice(0, visibleCount);

  return (
    <div className="content-wrapper">
      <div className="grid">
        {toRender.map((it) => (
          <ContentCard key={it.id || it._id || Math.random()} item={it} />
        ))}
      </div>

      {toRender.length === 0 && <div className="center">No items found.</div>}

      <div ref={loaderRef} className="loader">
        {visibleCount < filtered.length ? "Loading more..." : "No more items"}
      </div>
      <div className="count">Showing {toRender.length} of {filtered.length} items</div>
    </div>
  );
}
