import React, { useState, useEffect } from "react";
import Slider from "@mui/material/Slider";
import { useDispatch, useSelector } from "react-redux";
import { setPriceRange } from "../features/contentSlice"; // Redux action for price

export default function PriceSlider() {
  const dispatch = useDispatch();
  const filters = useSelector((s) => s.content.filters);

  const [range, setRange] = useState(filters.pricing.range || [0, 999]);
  const [paidSelected, setPaidSelected] = useState(filters.pricing.paid || false);

  useEffect(() => {
    setRange(filters.pricing.range || [0, 999]);
    setPaidSelected(filters.pricing.paid || false);
  }, [filters.pricing]);

  // Update URL query parameters
  const updateUrl = (newRange) => {
    const params = new URLSearchParams();

    if (filters.keyword) params.set("q", filters.keyword);
    if (paidSelected) params.set("paid", "1");
    if (filters.pricing.free) params.set("free", "1");
    if (filters.pricing.viewOnly) params.set("viewOnly", "1");
    if (filters.category && filters.category !== "All")
      params.set("category", filters.category);

    if (paidSelected) {
      params.set("minPrice", newRange[0]);
      params.set("maxPrice", newRange[1]);
    }

    const newUrl = window.location.pathname + "?" + params.toString();
    window.history.replaceState(null, "", newUrl);
  };

  // Slider value change
  const handleChange = (e, newValue) => {
    setRange(newValue);
    dispatch(setPriceRange(newValue)); // update Redux state
    updateUrl(newValue);
  };

  return (
    <div className="price-slider" style={{ display: "flex", alignItems: "center", gap: "10px" }}>
      <label>
        <input
          type="checkbox"
          checked={paidSelected}
          onChange={(e) => {
            setPaidSelected(e.target.checked);
            // optionally reset range when disabled
            if (!e.target.checked) handleChange(null, [0, 999]);
          }}
        /> Paid Items
      </label>
      <span>{range[0]}</span>
      <Slider
        value={range}
        onChange={handleChange}
        valueLabelDisplay="auto"
        min={0}
        max={999}
        disableSwap
        disabled={!paidSelected}
        style={{ flex: 1 }}
      />
      <span>{range[1]}</span>
    </div>
  );
}
