import React from "react";
import Slider from "@mui/material/Slider";
import { useDispatch, useSelector } from "react-redux";
import { setPricing } from "../features/contentSlice";

export default function PriceSlider() {
  const dispatch = useDispatch();
  const filters = useSelector((state) => state.content.filters);
  const range = filters.pricing.range || [0, 999];
  const paidSelected = filters.pricing.paid || false;

  // Update Redux and URL
  const handleChange = (e, newValue) => {
    dispatch(setPricing({ range: newValue }));
    updateUrl(newValue);
  };

  const handleCheckbox = (e) => {
    dispatch(setPricing({ paid: e.target.checked }));
    if (!e.target.checked) {
      dispatch(setPricing({ range: [0, 999] }));
      updateUrl([0, 999]);
    } else {
      updateUrl(range);
    }
  };

  const updateUrl = (newRange) => {
    const params = new URLSearchParams(window.location.search);
    if (paidSelected) {
      params.set("minPrice", newRange[0]);
      params.set("maxPrice", newRange[1]);
    } else {
      params.delete("minPrice");
      params.delete("maxPrice");
    }
    window.history.replaceState(null, "", window.location.pathname + "?" + params.toString());
  };

  return (
    <div style={{ display: "flex", alignItems: "center", gap: "10px" }}>
      <label>
        <input type="checkbox" checked={paidSelected} onChange={handleCheckbox} /> Paid Items
      </label>
      <span>{range[0]}</span>
      <Slider
        value={range}
        onChange={handleChange}
        valueLabelDisplay="auto"
        min={0}
        max={999}
        disableSwap
        disabled={!paidSelected}
        style={{ flex: 1 }}
      />
      <span>{range[1]}</span>
    </div>
  );
}
