import React, { useState, useEffect } from 'react';

export default function FilterPanel({ filters, onTogglePricing, onReset, onPriceRangeChange }) {
  const [localRange, setLocalRange] = useState(filters.priceRange || [0, 999]);

  useEffect(() => setLocalRange(filters.priceRange || [0, 999]), [filters.priceRange]);

  const handleCheckbox = (key) => {
    const payload = { [key]: !filters.pricing[key] };
    onTogglePricing(payload);
  };

  return (
    <div className="filter-panel">
      <div className="filter-section">
        <label><input type="checkbox" checked={!!filters.pricing.paid} onChange={() => handleCheckbox('paid')} /> Paid</label>
        <label><input type="checkbox" checked={!!filters.pricing.free} onChange={() => handleCheckbox('free')} /> Free</label>
        <label><input type="checkbox" checked={!!filters.pricing.viewOnly} onChange={() => handleCheckbox('viewOnly')} /> View Only</label>
      </div>

      <div className="filter-section">
        <label>Sort</label>
        <select value={filters.sort} onChange={(e) => onTogglePricing({ sort: e.target.value })}>
          <option value="name">Item Name (Default)</option>
          <option value="higher">Higher Price</option>
          <option value="lower">Lower Price</option>
        </select>
      </div>

      <div className="filter-section">
        <label>Price Range (0 - 999)</label>
        <div className="price-range">
          <input type="number" value={localRange[0]} min={0} max={localRange[1]} onChange={(e) => setLocalRange([Number(e.target.value || 0), localRange[1]])} />
          <input type="number" value={localRange[1]} min={localRange[0]} max={999} onChange={(e) => setLocalRange([localRange[0], Number(e.target.value || 999)])} />
          <button onClick={() => onPriceRangeChange(localRange)} disabled={!filters.pricing.paid} className="btn">Apply</button>
        </div>
        {!filters.pricing.paid && <small>Enable Paid to activate price range</small>}
      </div>

      <div className="filter-section">
        <button className="btn" onClick={onReset}>Reset</button>
      </div>
    </div>
  );
}
