import React, { useEffect, useMemo, useState, useRef } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { fetchData } from './features/dataSlice';
import { setPricing, resetFilters, setKeyword, setAll, setPriceRange } from './features/filtersSlice';
import FilterPanel from './components/FilterPanel';
import SearchBar from './components/SearchBar';
import ContentGrid from './components/ContentGrid';

const readFiltersFromUrl = () => {
  const params = new URLSearchParams(window.location.search);
  const pricing = {
    paid: params.get('paid') === '1',
    free: params.get('free') === '1',
    viewOnly: params.get('viewOnly') === '1',
  };
  const keyword = params.get('q') || '';
  const sort = params.get('sort') || 'name';
  const min = parseInt(params.get('min') || '0', 10);
  const max = parseInt(params.get('max') || '999', 10);
  return { pricing, keyword, sort, priceRange: [min, max] };
};

const writeFiltersToUrl = (filters) => {
  const params = new URLSearchParams();
  if (filters.pricing.paid) params.set('paid', '1');
  if (filters.pricing.free) params.set('free', '1');
  if (filters.pricing.viewOnly) params.set('viewOnly', '1');
  if (filters.keyword) params.set('q', filters.keyword);
  if (filters.sort && filters.sort !== 'name') params.set('sort', filters.sort);
  if (filters.priceRange) {
    params.set('min', String(filters.priceRange[0]));
    params.set('max', String(filters.priceRange[1]));
  }
  const newUrl = `${window.location.pathname}?${params.toString()}`;
  window.history.replaceState({}, '', newUrl);
};

export default function App() {
  const dispatch = useDispatch();
  const data = useSelector((s) => s.data.items);
  const dataStatus = useSelector((s) => s.data.status);
  const filters = useSelector((s) => s.filters);
  const [visibleCount, setVisibleCount] = useState(12);
  const loaderRef = useRef(null);

  useEffect(() => {
    dispatch(fetchData());
    const urlFilters = readFiltersFromUrl();
    dispatch(setAll(urlFilters));
  }, [dispatch]);

  useEffect(() => {
    writeFiltersToUrl(filters);
    setVisibleCount(12);
  }, [filters]);

  const filtered = useMemo(() => {
    if (!data) return [];
    const keyword = filters.keyword.trim().toLowerCase();

    const matchesPricing = (item) => {
      const isPaid = !!item.price;
      if (!filters.pricing.paid && !filters.pricing.free && !filters.pricing.viewOnly) return true;
      if (isPaid && filters.pricing.paid) {
        const price = Number(item.price || 0);
        if (price < filters.priceRange[0] || price > filters.priceRange[1]) return false;
        return true;
      }
      if (!isPaid && item.pricing?.toLowerCase?.() === 'free' && filters.pricing.free) return true;
      if (!isPaid && item.pricing?.toLowerCase?.() === 'view only' && filters.pricing.viewOnly) return true;
      return false;
    };

    let out = data.filter((item) => {
      const text = `${item.userName || ''} ${item.title || ''}`.toLowerCase();
      const kwMatch = !keyword || text.includes(keyword);
      return kwMatch && matchesPricing(item);
    });

    if (filters.sort === 'higher') {
      out.sort((a, b) => (Number(b.price || 0) - Number(a.price || 0)));
    } else if (filters.sort === 'lower') {
      out.sort((a, b) => (Number(a.price || 0) - Number(b.price || 0)));
    } else {
      out.sort((a, b) => ((a.title || '').localeCompare(b.title || '')));
    }

    return out;
  }, [data, filters]);

  useEffect(() => {
    const obs = new IntersectionObserver((entries) => {
      if (entries[0].isIntersecting) {
        setVisibleCount((v) => Math.min(v + 12, filtered.length));
      }
    }, { root: null, rootMargin: '200px', threshold: 0.1 });
    if (loaderRef.current) obs.observe(loaderRef.current);
    return () => obs.disconnect();
  }, [filtered.length]);

  const visibleItems = filtered.slice(0, visibleCount);

  return (
    <div className="app-root">
      <header className="app-header">
        <h1>CLO-SET Store - Starter</h1>
      </header>
      <div className="top-controls">
        <SearchBar value={filters.keyword} onChange={(val) => dispatch(setKeyword(val))} />
        <FilterPanel filters={filters} onTogglePricing={(payload) => dispatch(setPricing(payload))} onReset={() => dispatch(resetFilters())} onPriceRangeChange={(r) => dispatch(setPriceRange(r))} />
      </div>
      <main>
        {dataStatus === 'loading' && <div className="center">Loading data...</div>}
        {dataStatus === 'failed' && <div className="center error">Failed to load data</div>}
        <ContentGrid items={visibleItems} />
        <div ref={loaderRef} style={{ height: 1 }} />
        {visibleCount < filtered.length && <div className="center">Loading more...</div>}
        {filtered.length === 0 && dataStatus === 'succeeded' && <div className="center">No items found.</div>}
      </main>
      <footer className="app-footer">Data from closet-recruiting-api.azurewebsites.net</footer>
    </div>
  );
}
