import { Card, CardMedia, CardContent, Typography } from "@mui/material";

export default function ContentCard({ item }) {
  const isPaid = !!item.price;

  return (
    <Card sx={{ borderRadius: 3, boxShadow: 3 }}>
      <CardMedia
        component="img"
        height="200"
        image={item.previewImage || item.image || "https://via.placeholder.com/400x300?text=Image"}
        alt={item.title}
      />
      <CardContent>
        <Typography variant="subtitle2" fontWeight="bold">
          {item.userName || "Unknown"}
        </Typography>
        <Typography variant="body2" color="text.secondary">
          {item.title || "Untitled"}
        </Typography>
        <Typography variant="body1" color={isPaid ? "error" : "primary"} mt={1}>
          {isPaid ? `₹${item.price}` : (item.pricing || "Free/View")}
        </Typography>
      </CardContent>
    </Card>
  );
}
